+ function transfer(
+         address receiver,
+         uint256 id,
+         uint256 amount
+     ) public virtual returns (bool) {
+         require(receiver != address(0), "Zero address not allowed");
+         balanceOf[msg.sender][id] -= amount;
+ 
+         balanceOf[receiver][id] += amount;
+ 
+         emit Transfer(msg.sender, msg.sender, receiver, id, amount);
+ 
+         return true;
+     }

